import { Component } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import {Settings, SettingsInfo } from '../settings/settings';
import { Http } from '@angular/http';
import 'rxjs/add/operator/map';
import * as CryptoJS from 'crypto-js';
import * as app     from '../app'

interface CounterInfo {
  seconds : number;
  running : boolean;
}

interface TimerInfo {
  id        : number;
  installed : boolean;
}

@Component({
  selector: 'dr-timers',
  template: require('./timers.html'),
})
export class Timers {

  settings    : SettingsInfo;
  timers      : any = new Array<TimerInfo>();
  counters    = new Map<string, CounterInfo>();

  constructor(private http: Http) {
    this.settings = Settings.get();

    this.http.get(app.nodejsServer + '/timers/' + this.settings.branch)
    .map(response => response.json())
    .subscribe(response => {
      console.log('Data=' + JSON.stringify(response, undefined, 2));

      for (let i = 1; i <= this.settings.workstations; i++) {
        let ti : TimerInfo = {id : i, installed : response.timers.includes("" + i)};

        this.timers.push(ti);
      }
    });

    setInterval( ()=> {
      let mustBeep = false;

      this.counters.forEach((value, key, map) => {
        if (value.seconds != undefined) {
          if (value.running) {
            value.seconds--;
          }

          let minutes = Math.trunc(Math.abs(value.seconds) / 60);
          let seconds = Math.abs(value.seconds) % 60;

          $('#counter' + key).text('' + (value.seconds < 0 ? "-" : "") + (minutes < 10 ? "0" : "") + minutes + ":" + (seconds < 10 ? "0" : "") + seconds);

          if (value.seconds == 0) {
            $('#counterbutton' + key).css('background-color', 'red');
          }
          if (value.seconds < 0) {
            mustBeep = true;
          }
        } else {
          $('#counter' + key).text('Â·');
        }
      });
      if (mustBeep) {
        this.beep();
      }
    }, 1000);
  }

  beep() {
    let clip = new Audio("data:audio/wav;base64,");

    clip.volume = 1.0;
    clip.play();
  }

  executeAction(timer : string, action : string, minutes : number) {
    console.log("timer=" + timer);

    let seconds = ((minutes * 60) + 1);

    let counterInfo = this.counters.get(timer) || {seconds : undefined, running : false};

    if (action == 'start') {
      if (!counterInfo.running) {
        counterInfo.seconds = seconds;
        counterInfo.running = true;
        this.counters.set(timer, counterInfo);
        $('#counterbutton' + timer).css('background-color', 'white');
      }
    } else if (action == 'stop') {
      counterInfo.running = false;
      counterInfo.seconds = undefined;
      this.counters.set(timer, counterInfo);
      $('#counterbutton' + timer).css('background-color', 'white');
    } else if (action == 'pause') {
      counterInfo.running = false;
      this.counters.set(timer, counterInfo);
    } else if (action == 'resume') {
      if (counterInfo.seconds != undefined) {
        counterInfo.running = true;
        this.counters.set(timer, counterInfo);
      }
    }

    this.http.put(app.nodejsServer + '/timer/' + this.settings.branch + '/' + timer + '?action=' + action + '&seconds=' + seconds, undefined)
    .map(response => response.json())
    .subscribe(response => {
      console.log(JSON.stringify(response, undefined, 2));
      if (response.error) {
        console.log('Error!');
        $('#timer' + timer).addClass('btn-danger')
      }
    });

  }
}
